<?phpnamespace Materiales\Model\Entity;use Zend\Db\ResultSet\ResultSet;use Zend\Db\Adapter\Adapter;use Materiales\Model\Entity\Materiales;use Zend\Db\Sql\Sql;use Zend\ServiceManager\ServiceLocatorInterface;class Libros extends Materiales{   	  private $volumen;    private $edicion;    private $editorial;    private $tomo;    private $isbn;    public function __construct($datos = array(),$Adapter)        {        $this->id=$datos["codigo"].$datos['numero'].$datos["copia"];        $this->codigo=$datos["codigo"];        $this->titulo=$datos["titulo"];        $this->anio=$datos['anio'];        $this->autor=$datos["autor"];        $this->nro_pag=$datos["numero_paginas"];        $this->estado='disponible';        $this->descripcion=$datos["descripcion"];        $this->fecha_recepcion=$datos["date"];        $this->idioma=$datos["idioma"];        $this->numero=$datos["numero"];        $this->volumen=$datos["volumen"];        $this->edicion=$datos["edicion"];        $this->editorial=$datos["editorial"];        $this->copia=$datos["copia"];        $this->tomo=$datos["tomo"];        $this->isbn=$datos["isbn"];        $this->pal_claves=$datos['pal_clav'];        $this->tipo='Libro';        $this->tipo_tesis=null;        $this->dbAdapter=$Adapter;    }	public function getLibros()        {            $sql="select * from Materiales where tipo='Libro'";            $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);            return $result->toArray();        }        public function addLibro()        {            $sql=("insert into materiales (            id,codigo,titulo,autor,anio,numero_paginas,estado,descripcion,fecha_recepcion,idioma,            numero,volumen,edicion,editorial,copia,tomo,isbn,palabras_claves,tipo_material_nombre,tipo_tesis)values(            '".$this->id."','".$this->codigo."','".$this->titulo."','".$this->autor."','".$this->anio."','".$this->nro_pag."','".$this->estado.            "','".$this->descripcion."','".$this->fecha_recepcion."','".$this->idioma."','".$this->numero."','".$this->volumen."','".$this->edicion.            "','".$this->editorial."','".$this->copia."','".$this->tomo."','".$this->isbn."','".$this->pal_claves."','".$this->tipo."','".$this->tipo_tesis."')");           $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);          }	    public function updateLibro($codigo, $data=array())    {               $sql="update materiales set        titulo='".$this->titulo."',       autor='".$this->autor."',       anio='".$this->anio."',       numero_paginas='".$this->nro_pag."',       estado='".$this->estado."',       descripcion='".$this->descripcion."',       fecha_recepcion='".$this->fecha_recepcion."',       idioma='".$this->idioma."',       volumen='".$this->volumen."',       edicion='".$this->edicion."',       editorial='".$this->editorial."',       tomo='".$this->tomo."',       isbn='".$this->isbn."',       palabras_claves='".$this->pal_claves."'       where id=".$this->id."'";        $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);    }    public function eliminarLibro($codigo)    {        $sql="delete from materiales where id=".$this->id."'";        $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);    }  }?>