<?phpnamespace Materiales\Model\Entity;use Zend\Db\TableGateway\TableGateway;use Zend\Db\Adapter\Adapter;class Tesis extends Materiales{    private $tipo_tesis;    public function __construct($datos = array(),$adapter)        {        $this->id=$datos["codigo"].$datos['numero'].$datos["copia"];        $this->codigo=$datos["codigo"];        $this->titulo=$datos["titulo"];        $this->anio=$datos['anio'];        $this->autor=$datos["autor"];        $this->nro_pag=$datos["numero_paginas"];        $this->estado='disponible';        $this->descripcion=$datos["descripcion"];        $this->fecha_recepcion=$datos["date"];        $this->idioma=$datos["idioma"];        $this->numero=$datos["numero"];        $this->volumen=0;        $this->edicion=0;        $this->editorial=null;        $this->copia=$datos["copia"];        $this->tomo=0;        $this->isbn=0;        $this->pal_claves=$datos['pal_clav'];        $this->tipo='Tesis';        $this->tipo_tesis=$datos['tipo_tesis'];        $this->dbAdapter=$adapter;        }    public function getTesis()        {            $sql="select * from Materiales where tipo='Tesis'";            $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);            return $result->toArray();        }     public function addTesis()        {            $sql=("insert into materiales (            id,codigo,titulo,autor,anio,numero_paginas,estado,descripcion,fecha_recepcion,idioma,            numero,volumen,edicion,editorial,copia,tomo,isbn,palabras_claves,tipo_material_nombre,tipo_tesis)values(            '".$this->id."','".$this->codigo."','".$this->titulo."','".$this->autor."','".$this->anio."','".$this->nro_pag."','".$this->estado.            "','".$this->descripcion."','".$this->fecha_recepcion."','".$this->idioma."','".$this->numero."','".$this->volumen."','".$this->edicion.            "','".$this->editorial."','".$this->copia."','".$this->tomo."','".$this->isbn."','".$this->pal_claves."','".$this->tipo."','".$this->tipo_tesis."')");            $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);        }        public function updateTesis($id, $data=array())    {               $sql="update materiales set        titulo='".$this->titulo."',       autor='".$this->autor."',       anio='".$this->anio."',       numero_paginas='".$this->nro_pag."',       descripcion='".$this->descripcion."',       fecha_recepcion='".$this->fecha_recepcion."',       idioma='".$this->idioma."',       palabras_claves='".$this->pal_claves."'       where id=".$this->id."'";       $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);    }    public function eliminarTesis($id)    {       $sql="delete from materiales where id=".$this->id."'";        $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);    }  }