<?phpnamespace Usuarios\Model\Entity;use Zend\Db\TableGateway\TableGateway;use Zend\Db\Adapter\Adapter;class Carreras{    private $nombre;    private $descripcion;   	public function __construct($datos = array(),$adapter)		{        $this->nombre=$datos["nombre_carrera"];        $this->descripcion=$datos["descripcion_carrera"];        $this->dbAdapter=$adapter;        }     public function addCarrera()        {        $sql="insert into carreras (            nombre,descripcion)values(            '".$this->nombre."','".$this->descripcion."')";         $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);        }	    public function eliminarCarrera($nombre)    {        $sql="delete from carreras where nombre='".$nombre."'";        $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);    }  }?>