<?phpnamespace Usuarios\Model\Entity;use Zend\Db\TableGateway\TableGateway;use Zend\Db\Adapter\Adapter;class Tipo_usuario{    private $nombre;    private $materiales;    private $multas;    private $prestamo;    private $usuarios;    private $reservas;    private $informes;	public function __construct($datos = array(),$adapter)		{        $this->nombre=$datos["nombre_tipo"];        $this->materiales=$datos["materiales"];        $this->multas=$datos["multa"];        $this->prestamo=$datos["prestamo"];        $this->usuarios=$datos["usuario"];        $this->reservas=$datos["reservas"];        $this->informes=$datos["informes"];		$this->dbAdapter=$adapter;        }	    public function buscartipo($nombre)    {    	$sql="select * from tipo_usuario where nombre='".$nombre."'";        $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);        return $result->toArray();            }      public function addTipo()        {        $sql="insert into tipo_usuario (            nombre,admin_material,multas_material,registro_prestamo,registro_usuarios,reservas_material,emision_informes)            values(            '".$this->nombre."','".$this->materiales."','".$this->multas."','".$this->prestamo."','".$this->usuarios."','".$this->reservas."',            '".$this->informes."')";         $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);        }	    public function updatetipo($nombre)    {    $sql="update tipo_usuario set        nombre = '".$this->nombre."',       materiales =  '".$this->materiales."',       multas = '".$this->multas."',       prestamo = '".$this->prestamo."',       usuarios ='".$this->usuarios."',       reservas ='".$this->reservas."',       informes ='".$this->informes."',       where nombre='".$nombre."'";    $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);       //echo $sql;    }    public function eliminartipo($nombre)    {        $sql="delete from tipo_usuario where nombre='".$nombre."'";        $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);    }  }?>