<?phpnamespace Usuarios\Model\Entity;use Zend\Db\TableGateway\TableGateway;use Zend\Db\Adapter\Adapter;class Tipousuario{    private $nombre;    private $materiales;    private $multas;    private $prestamo;    private $usuarios;    private $reservas;    private $informes;	public function __construct($datos = array(),$adapter)		{        $this->nombre=$datos["nombre_tipo"];        $this->materiales=$datos["admin_material"];        $this->multas=$datos["multas_material"];        $this->prestamo=$datos["registro_prestamos"];        $this->usuarios=$datos["registro_usuario"];        $this->reservas=$datos["reservas_material"];        $this->informes=$datos["emision_informes"];		$this->dbAdapter=$adapter;        }	    public function buscartipo($nombre)    {    	$sql="select * from tipo_usuario where nombre='".$nombre."'";        $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);        return $result->toArray();            }      public function addTipo()        {         $sql="insert into tipo_usuario (            nombre,admin_material,multas_material,registro_prestamos,registro_usuarios,reservas_material,emision_informes)            values(            '".$this->nombre."','".$this->materiales."','".$this->multas."','".$this->prestamo."','".$this->usuarios."','".$this->reservas."',            '".$this->informes."')";         $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);        }	    public function updatetipo($nombre)    {    $sql="update tipo_usuario set        admin_material =  '".$this->materiales."',       multas_material = '".$this->multas."',       registro_prestamos = '".$this->prestamo."',       registro_usuarios ='".$this->usuarios."',       reservas_material ='".$this->reservas."',       emision_informes='".$this->informes."'       where nombre='".$nombre."'";    $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);       //echo $sql;    }    public function eliminartipo($nombre)    {        $sql="delete  from tipo_usuario where nombre='".$nombre."'";        $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);    }  }?>