<?phpnamespace Usuarios\Model\Entity;use Zend\Db\TableGateway\TableGateway;use Zend\Db\Adapter\Adapter;class Usuarios{    private $rut;    private $matricula;    private $nombre;    private $direccion;    private $fono;    private $correo;    private $carrera;    private $tipo;    private $password;	private $celular;	private $preg_sec;	private $respuesta;	public function __construct($datos = array(),$adapter)		{        $this->nombre=$datos["nombre"];        $this->rut=$datos["rut"];        $this->matricula=$datos["matricula"];        $this->direccion=$datos["direccion"];        $this->fono=$datos["fono"];        $this->correo=$datos["correo"];        $this->tipo=$datos["tipo"];        $this->password=$datos["password"];        $this->preg_sec=$datos["pregunta"];        $this->respuesta=$datos["respuesta"];        $this->celular=$datos["celular"];        $this->carrera=$datos["carrera"];		$this->dbAdapter=$adapter;        }	public function getUsuarios()        {            $sql="select * from Usuarios";            $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);            return $result->toArray();            //print_r($result);        }    public function buscarusuario($rut)    {    	$sql="select * from Usuarios where rut='".$rut."'";        $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);        return $result->toArray();            }      public function addUsuario()        {        $sql="insert into Usuarios (            rut,nombre,matricula,mail,telefono,password,direccion,            pregunta_secreta,respuesta_secreta,telefono_movil,            carreras_nombre,tipo_usuario_nombre)values(            '".$this->rut."','".$this->nombre."','".$this->matricula."','".$this->correo."','".$this->fono."','".$this->password."',            '".$this->direccion."','".$this->preg_sec."','".$this->respuesta."','".$this->celular."','".$this->carrera."','".$this->tipo."')";         $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);        }	    public function updateUsuario($rut)    {    $sql="update usuarios set        nombre = '".$this->nombre."',       direccion =  '".$this->direccion."',       telefono = '".$this->fono."',       mail = '".$this->correo."',       telefono_movil ='".$this->celular."'       where rut='".$rut."'";    $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);       //echo $sql;    }    public function eliminarUsuario($rut)    {        $sql="delete from usuarios where rut='".$rut."'";        $result=$this->dbAdapter->query($sql,Adapter::QUERY_MODE_EXECUTE);    }  }?>